import React from 'react';				/* React is JS lib buildon UI interactive apps Web/Mobile*/
import ReactDOM from 'react-dom';		/* ReactDOM */
import './index.css';


// Ceci est un composant car React.Component
// Square une classe/Type de composant React
//				- props (propriétés) = parameters
//				- render() 			 = method that returns React element
//				- onClick={() => alert('clic') = ele;ent interactif

//				- super dans les contructeurs d'une sous classe en JS

class Square extends React.Component {

	constructor(props)
	{
		super(props);
		this.state = {
			value: null,
		};
	}

	render() {
	  return (
		<button className="square" onClick={() => this.setState({value: 'X'})}>
		  {this.state.value}
		</button>
	  );
	}
  }


//  class Square extends React.Component {

// 	render() {
// 	  return (
// 		React.createElement('button', {className:'square', onClick:'{()=> alert("clic")}'}, this.props.value)
// 	  );
// 	}
//  }


// Fonction
// square is an array stored in Board
class Board extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			squares: Array(9).fill(null) }
		};

	renderSquare(i) {
	  return <Square value= {this.state.squares[i]} onClick={()=> this.HandleClick(i)}/>;
	}



	render() {
		const WinnerOfTheGame = calculateWinnerOfTheGame(this.state.squares);
		let status;
		if (WinnerOfTheGame) {
		  status = 'WinnerOfTheGame: ' + WinnerOfTheGame;
		} else {
		  status = 'Next player: ' + (this.state.xIsNext ? 'X' : 'O');
		}

		return (
		<div>
		  <div className="status">{status}</div>
		  <div className="board-row">
			{this.renderSquare(0)}
			{this.renderSquare(1)}
			{this.renderSquare(2)}
		  </div>
		  <div className="board-row">
			{this.renderSquare(3)}
			{this.renderSquare(4)}
			{this.renderSquare(5)}
		  </div>
		  <div className="board-row">
			{this.renderSquare(6)}
			{this.renderSquare(7)}
			{this.renderSquare(8)}
		  </div>
		</div>
	  );
	}
  }

class Game extends React.Component {
	render() {
	  return (
		<div className="game">
		  <div className="game-board">
			<Board />
		  </div>
		  <div className="game-info">
			<div>{/* status */}</div>
			<ol>{/* TODO */}</ol>
		  </div>
		</div>
	  );
	}
  }

  // ========================================

  const root = ReactDOM.createRoot(document.getElementById("root"));
  root.render(<Game />);
